package loops_forloop;

public class loop10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//0 to 100 odd
		
				for(int i=1; i<=100;i=i+2) {
					
					System.out.println(i);
				}
				
				// 100 to 0 odd
				
				for(int i=99;i>=1;i=i-2) {
					System.out.println(i);
				}
	}

}
