package string;

public class StringImmutable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// once we create an object after that java doesnot allow to perform changes but
		// if u are try to perform any changes those changes new object will create
		
		String str="rahul"; //19(indexid)
		String str1="python";
		String str2="java";
		String str4="amol";
		System.out.println(str);
		System.out.println(str1);
		System.out.println(str2);
		System.out.println(str4);
		
		str="ruby";//index id=28
		
		System.out.println(str);
		
		//security
		// code optimization and performance

	}

}
